import React, { Fragment, useEffect, useState } from "react";
import { HotTable } from "@handsontable/react";
import { colHeaders } from "./Constants";
import { modifyColWidth } from "./helpers/Helpers";
import "./Matrix.css";
import axios from "axios";
import { format } from "date-fns";
import { generalRenderer } from './MatrixRenderers';



const host = "data.usajobs.gov";
const userAgent = "mrezy1999";

function Matrix() {
  const [jobListings, setJobListings] = useState([]);


  const settings = {
    licenseKey: "non-commercial-and-evaluation",
    data: jobListings,
    rowHeaders: true,
    manualRowMove: true,
    modifyColWidth: modifyColWidth,
    manualColumnResize: true,
    contextMenu: true,
    colHeaders: colHeaders,
    width: "100%",
    height: 500,
    stretchH: "all",
    columnSorting: true,
    filters: true,
    dropdownMenu: true,
    cells: generalRenderer
  };

  useEffect(() => {
    const fetchData = async () => {
      const apiKey = process.env.REACT_APP_API_KEY;
      const endpoint = `https://${host}/api/search?Page=1&ResultsPerPage=200&Keyword=Internship`;
       const params = [
        {
          Keyword: "Developer",
          LocationName: "United States",

        }
      ];

      try {
        const response = await axios.get(endpoint, {
          params,
          headers: {
            "Authorization-Key": apiKey,
            "User-Agent": userAgent,
          },
        });

        const jobListings = response.data.SearchResult.SearchResultItems;
        setJobListings(
          jobListings.map((job) => [
            job.MatchedObjectDescriptor.DepartmentName,
            job.MatchedObjectDescriptor.PositionTitle,
            job.MatchedObjectDescriptor.PositionLocation[0].LocationName,
            job.MatchedObjectDescriptor.UserArea.Details.JobSummary,
            job.MatchedObjectDescriptor.PositionURI,
            format(new Date(job.MatchedObjectDescriptor.PositionStartDate), 'MM/dd/yyyy'),
            format(new Date(job.MatchedObjectDescriptor.PositionEndDate), 'MM/dd/yyyy'),
          ])
        );
      } catch (error) {
        console.error("Failed to retrieve job listings:", error);
      }
    };

    fetchData();
  }, []);

  return (
    <Fragment>
      <HotTable ref={jobListings} id="hot" settings={settings} />
      <hr />
      <div className="btn-group mb-5">
        {/* Additional buttons here */}
      </div>
    </Fragment>
  );
}


export default Matrix;
